router.beforeEach((to, from, next) => {
  // cookie上存了token，可以不用登录
  const token = getToken();
  if (token) {
    // 如果要跳转的页面是登录页，强制跳转到根页面
    if (to.path === LOGIN_PAGE_PATH) {
      next({ path: "/" });
