  }
}

function lasyLoadView (AsyncView) {
  const AsyncHandler = () => ({
    // component选项必须返回Promise
    // import('xxx.vue')会返回一个Promise
    component: AsyncView,
    loading: LoadingCom,
    error: ErrorCom,
